import struct
import zipfile
import base64

flag = b'grey{s0m3_Th1Ng5_4r3_b3tT3r_L3fT_uNz1Pp3d}'

def create_zip():
    with zipfile.ZipFile('shy_zipper.zip', 'w') as zf:
        zf.writestr('dont_unzip_me!.txt', 'you shouldn\'t have come here. but since you did, the flag is in base64, so don\'t you dare strings it')
        # spam nonsense to annoy stringers, unfortunately stringers still work :(
        zf.writestr('bm9uc2Vuc2VzdHVmZnRoYXR5b3VzaG91bGRudHRvdWNo', '')
        zf.writestr('ZXZlbm1vcmV1c2VsZXNzc3R1ZmZiZWNhdXNlc3RyaW5nc2lzYmFkCg==', '')
        zf.writestr('c3VwZXJhbm5veWluZ25vaXNldGhhdHJpbmdzaW55b3VyZWFyCg==', '')
        zf.writestr('aWxvdmVudXNncmV5aGF0c2FuZGlwcm9taXNldG9ub3R1c2VzdHJpbmdzYWdhaW4=', '')

    with open('shy_zipper.zip', 'rb') as f:
        data = f.read()
    
    eocd_sign = b'PK\x05\x06'
    eocd_offset = data.find(eocd_sign)
    
    if eocd_offset == -1:
        print('error reading zip file, eocd not present')
        return
    
    comment_len = struct.unpack('<H', data[eocd_offset + 20 : eocd_offset + 22])[0]
    eocd_end = eocd_offset + 22 + comment_len
    print('ok')
    
    with open('shy_zipper.zip', 'wb') as f:
        # adding null bytes to make it more obvious
        f.write(data + b'\x00\x00\x00\x00' + base64.b64encode(flag) + b'\x00\x00\x00\x00')
        
    print('done')
    
if __name__ == '__main__':
    create_zip()